mapper.select:
SELECT

FROM cms_category


        AND id = #{condition.id}


        AND site_id = #{condition.siteId}


        AND name = #{condition.name}


        AND parent_id = #{condition.parentId}


        AND type_id = #{condition.typeId}


        AND child_ids = #{condition.childIds}


        AND tag_type_ids = #{condition.tagTypeIds}


        AND code = #{condition.code}


        AND template_path = #{condition.templatePath}


        AND path = #{condition.path}


        AND only_url = #{condition.onlyUrl}


        AND has_static = #{condition.hasStatic}


        AND url = #{condition.url}


        AND content_path = #{condition.contentPath}


        AND contain_child = #{condition.containChild}


        AND page_size = #{condition.pageSize}


        AND allow_contribute = #{condition.allowContribute}


        AND sortno = #{condition.sortno}


        AND hidden = #{condition.hidden}


        AND contents = #{condition.contents}


        AND extend_id = #{condition.extendId}


        AND memo = #{condition.memo}


        AND dstatus = #{condition.dstatus}


        AND creator = #{condition.creator}


        AND create_date = #{condition.createDate}


        AND last_modifier = #{condition.lastModifier}


        AND last_mod_date = #{condition.lastModDate}


        AND version = #{condition.version}



ORDER BY ${orderBy} ${sortBy}

limit 1

