mapper.select:
SELECT

FROM uc_member


        AND id = #{condition.id}


        AND user_name = #{condition.userName}


        AND status = #{condition.status}


        AND rank = #{condition.rank}


        AND real_name = #{condition.realName}


        AND nick_name = #{condition.nickName}


        AND sex = #{condition.sex}


        AND birthday = #{condition.birthday}


        AND id_type = #{condition.idType}


        AND id_card = #{condition.idCard}


        AND avatar_url = #{condition.avatarUrl}


        AND tel = #{condition.tel}


        AND mobile = #{condition.mobile}


        AND email = #{condition.email}


        AND qq = #{condition.qq}


        AND home_phone = #{condition.homePhone}


        AND home_address = #{condition.homeAddress}


        AND intro = #{condition.intro}


        AND work_years = #{condition.workYears}


        AND profession = #{condition.profession}


        AND office_address = #{condition.officeAddress}


        AND province_id = #{condition.provinceId}


        AND city_id = #{condition.cityId}


        AND district_id = #{condition.districtId}


        AND reg_source = #{condition.regSource}


        AND reg_ip = #{condition.regIp}


        AND reg_time = #{condition.regTime}


        AND reg_area = #{condition.regArea}


        AND last_login_ip = #{condition.lastLoginIp}


        AND last_login_time = #{condition.lastLoginTime}


        AND last_login_area = #{condition.lastLoginArea}


        AND mobile_verification = #{condition.mobileVerification}


        AND email_verification = #{condition.emailVerification}


        AND invite_code = #{condition.inviteCode}


        AND inviter = #{condition.inviter}


        AND is_agent = #{condition.isAgent}


        AND memo = #{condition.memo}


        AND dstatus = #{condition.dstatus}


        AND creator = #{condition.creator}


        AND createDate = #{condition.createDate}


        AND lastModifier = #{condition.lastModifier}


        AND lastModDate = #{condition.lastModDate}


        AND version = #{condition.version}



ORDER BY ${orderBy} ${sortBy}

limit 1

